import * as React from 'react';
import { Text, View, StyleSheet, Button, ImageBackground, TouchableOpacity, TextInput } from 'react-native';

class CreateUpdateComponent extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      itemList: this.props.navigation.state.params.itemList,
      input: "",
      buttonType: this.props.navigation.state.params.buttonType
    }
  }

  handleChange = (e) => {
    this.setState({
      input: e.target.value
    })
  }

  render() {
    return (
      <View style={styles.container}>
        <TextInput style={styles.input} onChange={this.handleChange}/>
        <TouchableOpacity onPress={() => {
                      let arr = this.state.itemList
                      let largest = 0;
                      for (let i = 0; i<this.state.itemList.length; i++) {
                        if (parseInt(this.state.itemList[i].id)>largest) {
                          largest = this.state.itemList[i].id;
                        }
                      }
                      if (this.state.input !== "") {
                        arr.push({
                                id: largest+1, 
                                task: this.state.input, 
                                checked: false, 
                                minute: 25, 
                                second: "00"
                                })
                        this.props.navigation.navigate('Dashboard', {array: arr})
                      } 
                      else {
                        this.props.navigation.navigate('Dashboard', {array: this.state.itemList})
                      }

                      }
                      }>
          <Text>{this.state.buttonType}</Text>
        </TouchableOpacity>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center"
  },
  input: {
    marginRight: 10,
    height: 20, borderColor: 'gray', borderWidth: 1
  }

});

export default CreateUpdateComponent;
