import * as React from 'react';
import { Text, View, StyleSheet, Button, ImageBackground, TouchableOpacity } from 'react-native';
import { CheckBox } from 'react-native-elements';
import moment from "moment";
import AppLoading from 'expo-app-loading';
import * as Font from 'expo-font';

let customFonts = {
  'Fredoka One': require('../assets/fonts/FredokaOne-Regular.ttf')
};

let interval = []

let counter = 0

const image = { uri: "https://i.pinimg.com/originals/c2/dd/7b/c2dd7bf1a4602978cb41f154d551a5ff.jpg" };

class FocusComponent extends React.Component {

  constructor(props) {
    super(props)

    if (props.navigation.state.params === undefined) {
      this.state = {
        eventDate:moment.duration().add({days:0,hours:0,minutes:25,seconds:"00"}),
        days:0,
        hours:0,
        data: [],
        mins: 25,
        secs: "00",
        task: "Default Task",
        id: 0,
        fontsLoaded: false,
      }
    }
    else {
      console.log(props.navigation.state.params)
      // let propsMin = parseInt(props.navigation.state.params.minute)
      // let propsSec = props.navigation.state.params.second
      // let propsTask = props.navigation.state.params.task
      // let propsData = props.navigation.state.params.data
      // let propsId = props.navigation.state.params.id
      this.state = {
        eventDate:moment.duration().add({days:0,hours:0,minutes:propsMin,seconds:propsSec}),
        days:0,
        hours:0,
        data: propsData,
        mins: propsMin,
        secs: propsSec,
        task: propsTask,
        id: propsId
      }
    }
  }

  componentDidMount() {
    this._loadFontsAsync();
  }

  async _loadFontsAsync() {
    await Font.loadAsync(customFonts);
    this.setState({ fontsLoaded: true });
  }

  stopTimer=()=> {
    counter = 0
    for (let i = 0; i < interval.length; i++) {
        clearInterval(interval[i])
    }
  }

  startTimer=()=>{
    counter += 1
    if (counter === 1) {
      const x=setInterval(()=>{
      let {eventDate} = this.state
      if(eventDate <=0){
        clearInterval(x)
      }
      else {
        eventDate = eventDate.subtract(1,"s")
        const days = eventDate.days()
        const hours = eventDate.hours()
        const mins = eventDate.minutes()
        const secs = eventDate.seconds()

        this.setState({
          days,
          hours,
          mins,
          secs,
          eventDate
        })
      }
    },1000)
    interval.push(x)
    }
  }

  resetTimer=()=> {
    counter = 0
    for (let i = 0; i < interval.length; i++) {
        clearInterval(interval[i])
    }
    this.setState({
      eventDate:moment.duration().add({days:0,hours:0,minutes:25,seconds:0}),
      days: 0,
      hours: 0,
      mins: 25,
      secs: "00"
    })
  }

  render() {
    const { mins, secs } = this.state

    return (
      <View style={styles.container}>
        <ImageBackground source={image} style={styles.image} imageStyle={{opacity:0.5}}>
          <View style={styles.back}>
            <TouchableOpacity onPress={() => {  counter = 0
                                                for (let i = 0; i < interval.length; i++) {
                                                            clearInterval(interval[i])
                                                        }
                                                  console.log(this.state.mins)
                                                  console.log(this.state.secs)
                                                  console.log(this.state.id)
                                                let data = this.state.data
                                                for (let i = 0; i<data.length; i++) {
                                                  if (data[i].id === this.state.id) {
                                                    data[i].minute = this.state.mins
                                                    data[i].second = this.state.secs
                                                  }
                                                }
                                                console.log(data)
                                                this.props.navigation.navigate('Dashboard', 
                                                {array:data})}
                                                }>
              <Text>Back</Text>
            </TouchableOpacity>
          </View>
          <View style={styles.top}>
            <TouchableOpacity onPress={this.startTimer}>
              <Text>Start</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={this.stopTimer}>
              <Text>Stop</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={this.resetTimer}>
              <Text>Reset</Text>
            </TouchableOpacity>
          </View>
          <View style={styles.middle}>
            <Text style={styles.timer}>{ `${mins} : ${secs}`}</Text>
          </View>
          <View style={styles.bottom}>
            <TouchableOpacity onPress={() => {  counter = 0
                                                for (let i = 0; i < interval.length; i++) {
                                                            clearInterval(interval[i])
                                                        }
                                                  console.log(this.state.mins)
                                                  console.log(this.state.secs)
                                                  console.log(this.state.id)
                                                let data = this.state.data
                                                for (let i = 0; i<data.length; i++) {
                                                  if (data[i].id === this.state.id) {
                                                    data[i].minute = this.state.mins
                                                    data[i].second = this.state.secs
                                                    data[i].checked = true
                                                  }
                                                }
                                                console.log(data)
                                                this.props.navigation.navigate('Dashboard', 
                                                {array:data})}
                                                }>
              <Text>CHECKBOX</Text>
            </TouchableOpacity>
            <Text>{this.state.task}</Text>
          </View>
        </ImageBackground>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  image: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center"
  },
  container: {
    flex: 1,
    backgroundColor: "white",
    justifyContent: "center",
  },
  back: {
    position: "relative",
    bottom: 150,
    left: 50
  },
  top: {
    marginRight: 60,
    flexDirection: 'row',
    alignItems: "flex-end",
    justifyContent: "flex-end",
  },
  middle: {
    alignItems: "center",
    justifyContent: "center",
  },
  bottom: {
    flexDirection: 'row',
    alignItems: "center",
    justifyContent: "space-evenly",
  },
  timer: {
    fontFamily: 'Fredoka One',
    fontSize: 80,
    fontWeight: "bold"
  }
});

export default FocusComponent;
